CREATE TABLE student_fitness_test_results (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "UniversityId" integer NOT NULL,
    "AcademicYearId" integer NOT NULL,
    "CourseId" integer NOT NULL,
    "CourseSectionMeetingId" integer NOT NULL,
    "StudentCourseEnrollmentId" integer NOT NULL,
    "DegreeDivisionId" integer NOT NULL,
    "ExerciseId" integer NOT NULL,
    "AgeStageId" integer,
    "EvaluationId" integer,
    "AttemptNo" smallint NOT NULL DEFAULT 1,
    "TestDatetime" timestamp with time zone NOT NULL DEFAULT (NOW()),
    "PerformedValue" numeric(6,2) NOT NULL,
    "AchievedDegree" numeric(6,2),
    "AchievedPercentage" numeric(5,2),
    "IsAbsent" boolean NOT NULL DEFAULT FALSE,
    "Notes" text,
    "IsActive" boolean NOT NULL DEFAULT TRUE,
    "InsertUserCode" character varying(100),
    "InsertDate" timestamp with time zone NOT NULL DEFAULT (NOW()),
    "UpdateUserCode" character varying(100),
    "LastUpdate" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    "DeleteUserCode" character varying(100),
    "DeleteDate" timestamp with time zone,
    CONSTRAINT "PK_student_fitness_test_results" PRIMARY KEY ("Id"),
    CONSTRAINT ck_student_fitness_result_achieved_degree CHECK (achieved_degree IS NULL OR achieved_degree >= 0),
    CONSTRAINT ck_student_fitness_result_achieved_percentage CHECK (achieved_percentage IS NULL OR (achieved_percentage >= 0 AND achieved_percentage <= 100)),
    CONSTRAINT ck_student_fitness_result_attempt_no CHECK (attempt_no > 0),
    CONSTRAINT ck_student_fitness_result_performed_value CHECK (performed_value >= 0),
    CONSTRAINT fk_student_fitness_result_academic_year FOREIGN KEY ("AcademicYearId") REFERENCES "AcademicYear" ("Id") ON DELETE RESTRICT,
    CONSTRAINT fk_student_fitness_result_age_stage FOREIGN KEY ("AgeStageId") REFERENCES lookup_fitness_age_stages ("Id") ON DELETE RESTRICT,
    CONSTRAINT fk_student_fitness_result_course FOREIGN KEY ("CourseId") REFERENCES "Course" ("Id") ON DELETE RESTRICT,
    CONSTRAINT fk_student_fitness_result_degree_division FOREIGN KEY ("DegreeDivisionId") REFERENCES "LookupCourseDegreeDevision" ("Id") ON DELETE RESTRICT,
    CONSTRAINT fk_student_fitness_result_evaluation FOREIGN KEY ("EvaluationId") REFERENCES lookup_fitness_exercise_evaluations ("Id") ON DELETE SET NULL,
    CONSTRAINT fk_student_fitness_result_exercise FOREIGN KEY ("ExerciseId") REFERENCES lookup_fitness_exercises ("Id") ON DELETE RESTRICT,
    CONSTRAINT fk_student_fitness_result_meeting FOREIGN KEY ("CourseSectionMeetingId") REFERENCES "CourseSectionMeeting" ("Id") ON DELETE RESTRICT,
    CONSTRAINT fk_student_fitness_result_student_enrollment FOREIGN KEY ("StudentCourseEnrollmentId") REFERENCES "StudentCourseEnrollment" ("Id") ON DELETE RESTRICT,
    CONSTRAINT fk_student_fitness_result_university FOREIGN KEY ("UniversityId") REFERENCES "University" ("Id") ON DELETE RESTRICT
);
COMMENT ON TABLE student_fitness_test_results IS 'جدول تسجيل نتائج اختبارات اللياقة البدنية للطلبة على مستوى المادة والمحاضرة';
